# Map 1-based optional input ports to variables
dataset1 <- maml.mapInputPort(1) # class: data.frame
library(ggplot2)
factor(dataset1$A_S_Label)
Ashford_D <- ggplot(dataset1, aes(x=Ashford, fill=A_S_Label)) + geom_density(alpha=.3) + ggtitle("Ashford University") + labs(x="Probability", y="Density") + theme(plot.title = element_text(color="#111111", size=12, hjust=.5)) + theme(axis.title = element_text(color="#111111", size=10)) + scale_fill_discrete(name="Application Status")
Ashford_H <- ggplot(dataset1, aes(x=Ashford, fill=A_S_Label)) + geom_histogram(position="identity", alpha=0.4, bins = 100) + scale_y_log10() + ggtitle("Ashfrod University") + labs(x="Probability", y="Total Count") + theme(plot.title = element_text(color="#111111", size=12, hjust=.5)) + theme(axis.title = element_text(color="#111111", size=10)) + scale_fill_discrete(name="Application Status")
